import React, { Component } from "react";
import logo from "./telos.png";
import "./App.css";
import ScatterJS from "scatterjs-core";
import ScatterEOS from "scatterjs-plugin-eosjs2";
import { Api, JsonRpc } from 'eosjs';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      scatterjs: null,
      network: null
    };
  }

  componentDidMount() {
    ScatterJS.plugins(new ScatterEOS());

    const network = {
      blockchain: "eos",
      protocol: "http",
      host: "127.0.0.1",
      port: 8888,
      chainId:
        "5c414e20cb772e0d634a3a1e54587bc49ea5b3a11c1991dc4548f93d72f9621f"
    };

    this.setState({
      network: network,
      scatterjs: ScatterJS
    });

    // window.ScatterJS = null;
    // window.ScatterEOS = null;
  }
  onClick() {
    ScatterJS.scatter.connect("My-App").then(connected => {
      if (!connected) return false;

      const scatter = ScatterJS.scatter;
      const requiredFields = { accounts: [this.state.network] };

      scatter.getIdentity(requiredFields).then(async () => {

        const account = scatter.identity.accounts.find(x => x.blockchain === "eos");

          // You can pass in any additional options you want into the eosjs reference.
          const eosOptions = { expireInSeconds: 60 };

          // Get a proxy reference to eosjs which you can use to sign transactions with a user's Scatter.

          const rpc = new JsonRpc("http://127.0.0.1:8888");

          const eos = scatter.eos(this.state.network, Api, { rpc });

          // const transactionOptions = { authorization:[`${account.name}@${account.authority}`] };
          // console.log(requiredFields);
          await eos.transact(
            {
              actions: [
                {
                  account: "ed1111111111",
                  name: "hello",
                  authorization: [
                    {
                      actor: account.name,
                      permission: account.authority
                    }
                  ],
                  data: {}
                }
              ]
            },
            { blocksBehind: 10, expireSeconds: 30 }
          );
        })
        .catch(error => {
          // The user rejected this request, or doesn't have the appropriate requirements.
          console.error("ERROR:", error);
        });
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <button onClick={() => this.onClick}>Test</button>
        </header>
      </div>
    );
  }
}
