import React, { Component } from "react";
import logo from "./telos.png";
import "./App.css";
import ScatterBridge  from "./scatterBrigde";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      scatterBridge: null,
      network: {
        blockchain: "eos",
        protocol: "http",
        host: "127.0.0.1",
        port: 8888,
        chainId:
          "5c414e20cb772e0d634a3a1e54587bc49ea5b3a11c1991dc4548f93d72f9621f"
      }
    };

    this.onClick = this.onClick.bind(this);
  }

  componentDidMount() {
    this.setState({
      scatterBridge: new ScatterBridge(this.state.network, "My-App")
    });
  }

  async onClick(arg) {
    var actions = [];
    await this.state.scatterBridge.getIdentity();

    // actions.push(this.state.scatterBridge.makeAction("ed1111111111","hello", {}));
    // actions.push(this.state.scatterBridge.makeAction("ed1111111111","bye", {}));
    // actions.push(this.state.scatterBridge.makeAction("ed1111111111", "test", {
    //     bets: [1, 2, 3, 3, 2, 1],
    //     recharges: 3,
    //     seed_phrase: "testing"
    //   }, "owner"));

    // await this.state.scatterBridge.sendTx(actions);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <button onClick={this.onClick}>Test</button>
        </header>
      </div>
    );
  }
}

export default App;
