import React, { Component } from "react";
import logo from "./telos.png";
import "./App.css";
import ScatterJS from "scatterjs-core";
import ScatterEOS from "scatterjs-plugin-eosjs2";
// import Eos from 'eosjs';
import { Api, JsonRpc, JsSignatureProvider } from 'eosjs';
// import { JsSignatureProvider } from 'eosjs';

class App extends Component {
  constructor(props) {
    super(props);

  }

  componentDidMount() {
    ScatterJS.plugins(new ScatterEOS());
    const connectionOptions = { initTimeout: 10000 };

    const network = {
      blockchain: "eos",
      protocol: "http",
      host: "127.0.0.1",
      port: 8888,
      chainId:"4cec7eac46c16a031768556a675aff2794f56868d290a868a5981b4f0c07d42c"
    };

    ScatterJS.scatter.connect("My-App").then(connected => {
      // If the user does not have Scatter or it is Locked or Closed this will return false;
      if (!connected) return false;

      const scatter = ScatterJS.scatter;

      // Now we need to get an identity from the user.
      // We're also going to require an account that is connected to the network we're using.
      const requiredFields = { accounts: [network] };

      scatter .getIdentity(requiredFields).then(async () => {



          // Always use the accounts you got back from Scatter. Never hardcode them even if you are prompting
          // the user for their account name beforehand. They could still give you a different account.

          const account = scatter.identity.accounts.find(x => x.blockchain === 'eos');

          // You can pass in any additional options you want into the eosjs reference.
          const eosOptions = { expireInSeconds: 60 };

          // Get a proxy reference to eosjs which you can use to sign transactions with a user's Scatter.

          // const defaultPrivateKey = "5KENusrZoy5bw9hT8s74Wv3MDdHyqsvsHzVzeRL5LtYXR6MZRcS";
          // const signatureProvider = new JsSignatureProvider([defaultPrivateKey]);
          const rpc = new JsonRpc('http://127.0.0.1:8888');
          // const api = Api({ rpc, signatureProvider, textDecoder: new TextDecoder(), textEncoder: new TextEncoder() });
          // const api = new Api({ rpc, signatureProvider:scatter.eosHook(network) });
          const eos = scatter.eos( network, Api, {rpc} );
          console.log(eos)
          // const eos = Eos({httpEndpoint:'',  signatureProvider:ScatterJS.scatter.eosHook(network)});

          // ----------------------------
          // Now that we have an identity,
          // an EOSIO account, and a reference
          // to an eosjs object we can send a transaction.
          // ----------------------------

          // Never assume the account's permission/authority. Always take it from the returned account.
          // console.log(account);

          const transactionOptions = { authorization:[`${account.name}@${account.authority}`] };
          // console.log(requiredFields);

          // const contract = await eos.contract('ed11111111', {requiredFields});
          // const result = await contract.hi(...);
          // console.log(contract);


          // eos.transfer(account.name, 'helloworld', '1.0000 EOS', 'memo', transactionOptions).then(trx => {
          //     // That's it!
          //     console.log(`Transaction ID: ${trx.transaction_id}`);
          // }).catch(error => {
          //     console.error(error);
          // });
        })
        .catch(error => {
          // The user rejected this request, or doesn't have the appropriate requirements.
          console.error("ERROR:", error);
        });
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
      </div>
    );
  }
}
